void Szkola::setNazwa(string nazwa) {
    this->nazwa = nazwa;
}

void Szkola::setNumer(int numer) {
    this->numer = numer;
}

Szkola::Szkola(string nazwa, int numer) {
    this->nazwa = nazwa;
    this->numer = numer;
}

