void Szkola::getKlasy() {
    for(int i = 0 ; i < klasy.size();i++){
        cout<<"Klasy :"<<"\n"<<klasy[i]->getNumer()<<"\n";
    }
}

void Szkola::dodajKlase(Klasa* klasa) {
    klasy.push_back(klasa);
}

void Szkola::usunKlase(int numer) {
    for(int i = 0 ; i < klasy.size();i++){
        if(i == numer){
            klasy.erase(klasy.begin()+i);
        }
    }
}
